$(function() {
    var http = 'http://';
    var semicolon = ';';
    for (var i in audioData0) {
        if(audioData0[i].mp3){
            if (audioData0[i].mp3.indexOf(http) < 0) { //如果没有http://
                audioData0[i].mp3 = http + audioData0[i].mp3;
            }
            if (audioData0[i].mp3.indexOf(semicolon) !== -1) { //如果有;
                audioData0[i].mp3 = audioData0[i].mp3.replace(semicolon, '');
            }            
        }
    }
    var audioPlayListVar = new jPlayerPlaylist({
        jPlayer: "#jquery_jplayer_1",
        cssSelectorAncestor: "#jp_container_1"
    }, audioData0, {
        swfPath: "http://www.news.cn/2015/txh2015/audio/js/",
        supplied: "m4v, mp3",
        wmode: "window",
        useStateClassSkin: true,
        autoBlur: false,
        smoothPlayBar: true,
        keyEnabled: false,
        remainingDuration: true,
        volume: 1.0,
        ready: function() {
            $(this).jPlayer("repeat");
            var $jpDetails = $('.jp-details');
            $jpDetails.show();
        }
    });
    //pause
    var $audioPause = $("#audioPause");
    $audioPause.on("click", function() {
        audioPlayListVar.pause();
    });
});